sap.ui.define(["sap/ui/core/mvc/Controller"],function(t){"use strict";return t.extend("ui5-pwa.controller.App",{onInit:function(){this.resetNewTodo()},addNewTodo:function(){const t=this.getNewTodo();this.addTodo(t);this.resetNewTodo()},addTodo:function(t){if(t.text.substring(0,1)==="!"){t.important=true;t.text=t.text.substring(1)}if(t.text===""){return}const e=this.getView().getModel("todos");const o=e.getData();o.push(t);e.setData(o);this.saveTodosToLocalStorage(o)},setDone:function(){const t=this.getView().getModel("todos").getData();this.saveTodosToLocalStorage(t)},saveTodosToLocalStorage:function(t){if(localStorage){localStorage.setItem("todos",JSON.stringify(t))}},resetNewTodo:function(){this.getView().getModel("newTodo").setData({text:"",checked:false,important:false})},getNewTodo:function(){return this.getView().getModel("newTodo").getData()}})});